const FormInput = ({label, ...otherProps}) => {
  return (
    <div className='group'>
         <label className={`${otherProps.value.length > 0 ? 'shrink' : ''} form-input-label `}>{label}</label><br />
            <input  {...otherProps}/>
    </div>
  )
}

export default FormInput
